mapper.insert:
INSERT INTO eco.company 
VALUES (ECO.SEQ_COMP_SEQ.nextval,#{cust_id},#{comp_name},#{comp_num},#{addr_post},
#{addr_city},#{addr_detail}, default, default, #{cust_id}, default, #{cust_id},#{memo}
)

mapper.update:
update eco.company
set addr_post=#{addr_post}, addr_city=#{addr_city}, addr_detail=#{addr_detail},
editUser=#{cust_id}, editDate=default, memo=#{memo}
WHERE comp_seq = #{comp_seq}
